import React, { useState } from "react";

import { jsPDF } from "jspdf";

import { marked } from "marked";



const App = () => {

  const [inputValues, setInputValues] = useState({

    texto1: "",

    texto2: "",

    texto3: "",

  });



  const handleChange = (e) => {

    const { name, value } = e.target;

    setInputValues({ ...inputValues, [name]: value });

  };



  const generateMarkdown = () => {

    // meter mark

    return `

# formulario



| **Campo** | **Valor**      |

|-----------|----------------|

| texto 1   | ${inputValues.texto1} |

| texto 2   | ${inputValues.texto2} |

| texto 3   | ${inputValues.texto3} |

`;

  };



  const generatePDF = () => {

    // pasar a md

    const markdownContent = generateMarkdown();



    // Cpasar de md a HTML

    const htmlContent = marked(markdownContent);



    // Crear el PDF

    const pdf = new jsPDF();

    pdf.html(htmlContent, {

      callback: function (doc) {

        doc.save("tableroformulario.pdf");

      },

      x: 10,

      y: 10,

    });

  };



  return (

    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>

      <h1>formulario</h1>

      <div style={{ marginBottom: "10px" }}>

        <label>texto 1: </label>

        <input

          type="text"

          name="texto1"

          value={inputValues.texto1}

          onChange={handleChange}

        />

      </div>

      <div style={{ marginBottom: "10px" }}>

        <label>texto 2: </label>

        <input

          type="text"

          name="texto2"

          value={inputValues.texto2}

          onChange={handleChange}

        />

      </div>

      <div style={{ marginBottom: "10px" }}>

        <label>texto 3: </label>

        <input

          type="text"

          name="texto3"

          value={inputValues.texto3}

          onChange={handleChange}

        />

      </div>

      <button onClick={generatePDF} style={{ padding: "10px 20px", marginRight: "10px" }}>

        generar PDF

      </button>


    </div>

  );

};



export default App;
